                // onDeleteUserAcquisition={handleDeleteUserAcquisition}
                onDeleteAcquisition={onDeleteAcquisition}
                // setUserAcquisitions={setUserAcquisitions}
              />
          })    

    return (
        <div className="storage" >
          <h4>My Work</h4>
              {uArtworkCard}
          <h4>Collection</h4>
              {uAcquisitionCard}
        </div>
    );
}

