import React from 'react';
import { NavLink } from 'react-router-dom';
import {
  Menu
} from 'semantic-ui-react'

function NavBar({ currentUser, setCurrentUser }) {

    function logout(){
        setCurrentUser(null);
    }

    return(
            <div class="ui top attached menu">
                <div class="ui dropdown item">
                <i class="raregifs"><NavLink to="/" exact className="button">Raregifs</NavLink></i>
                    <div class="menu">
                        <div class="item">
                            <i class="dropdown icon"></i>
                        </div>
                        <NavLink to="/upload" exact className="button">
                            Upload
                        </NavLink>
                        <NavLink to="/storage" exact className="button">
                            Storage
                        </NavLink>
                        {currentUser ? (
                            <>
                                <button onClick={logout} >logout</button>   
                                <NavLink to="/account" exact className="button">
                                    Account
                                </NavLink>
                            </>
                        ) : (
                            <>
                                <NavLink to="/login" exact className="button">
                                    Login
                                </NavLink>
                                <NavLink to="/signup" exact className="button" >
                                    Signup
                                </NavLink>
                            </>
                        )}
                    </div>
                </div>
            </div>
    ); 
}

export default NavBar;
