import React, { useEffect, useState } from 'react';
// import React from 'react';
import StorageList from './StorageList'

function Storage({ 
  currentUser,
  // acquisitions,
  // setAcquisitions,
  // onDeleteAcquisition,
  // artworks,
  // setArtworks,
  // onDeleteArtwork,
  // onUpdateArtwork,
 }) {

  const [currentUserAcquisitions, setCurrentUserAcquisitions] = useState([])
  const [currentUserArtworks, setCurrentUserArtworks] = useState([]) 
  const { id } = currentUser

  useEffect(() => {
    fetch(`http://localhost:3000/users/${id}/acquisitions`)
        .then(r => r.json())
        .then(userAcquisitionsArray => {
            setCurrentUserAcquisitions(userAcquisitionsArray);
        })
  }, [])

  useEffect(() => {
    fetch(`http://localhost:3000/users/${id}/artworks`)
        .then(r => r.json())
        .then(userArtworksArray => {
            setCurrentUserArtworks(userArtworksArray);
        })
  }, [])

  function handleDeleteAcquisition(id) {
    // i have the id for the acquisition i want to delete but not the user_id (but doesnt acquisition have the user_id?)
    // const updatedAcquisitionsArray = user.acquisitions.filter((acquisition) => acquisition.id !== id);
    const updatedAcquisitionsArray = acquisitions.filter((acquisition) => acquisition.id !== id);
    setAcquisitions(updatedAcquisitionsArray);
    // setUsers(updatedAcquisitionsArray);
  } 

  function handleUpdateArtwork(updatedArtwork) {
    const updatedArtworksArray = artworks.map((artwork) => {
      if (artwork.id === updatedArtwork.id) {
          return updatedArtwork;
      } else {
          return artwork;
      }
    })
    setArtworks(updatedArtworksArray);
  }

  function handleDeleteArtwork(id) {
    const updatedArtworksArray = artworks.filter((artwork) => artwork.id !== id);
    setArtworks(updatedArtworksArray);
  }  

  return (
      <div className="storage" >
        <StorageList
           currentUser={currentUser}
           acquisitions={acquisitions}
           setAcquisitions={setAcquisitions}
           onDeleteAcquisition={onDeleteAcquisition}
           artworks={artworks}
           setArtworks={setArtworks}
           onDeleteArtwork={onDeleteArtwork}
           onUpdateArtwork={onUpdateArtwork}
        />
      </div>
  );
}

export default Storage;
