import React from 'react';
import { 
    Container,
    Card,
    Image,
    Button,
  } from 'semantic-ui-react'

function UserAcquisitionCard({ 
                aArtwork, 
                cUId, 
                onAcquireArtwork,
             }) {

    function handleAcquireArtworkClick(e) {
        e.preventDefault();
        fetch('http://localhost:3000/acquisitions', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user_id: cUId,
                artwork_id: aArtwork.id
            }),
        })
            .then(r => r.json())
            .then((newUserAcq) => onAcquireArtwork(newUserAcq))
    }

    return (
        // <Container 
        //     className='user-container'  
        //     style={{
        //         // flexWrap: 'wrap',
        //         // position: 'relative',
        //         // alignItems: 'center',
        //         // textAlign:'center',
        //         // justifyContent: 'center',
        //         // padding: '10px', 
        //         }}
        //     >

        <Card.Group>
            <style>
                {`
                html, body {
                    background-color: #252839 !important;
                }
                `}
            </style>
            <Card 
                itemsPerRow={4}
                style={{
                    // display: 'flex',
                    // width: '250px',
                    // textAlign:'center',
                    // justifyContent:'space-around',
                }}
            >
                <Card.Content>
                    <Image src={aArtwork.image} alt={aArtwork.title} />
                    <Card.Header
                        style={{
                            fontSize: 'small',
                        }}
                    >
                        {aArtwork.title}
                    </Card.Header>
                    <Card.Meta>
                        <span className='artist-name'>{aArtwork.user_name}</span>
                    </Card.Meta>
                </Card.Content>
                    {cUId ? (
                        <>
                        <Button size='small' onClick={handleAcquireArtworkClick}>
                            acquire
                        </Button>
                        </>
                    ) : (
                        <>
                        </>
                    )}
                <br></br>
            </Card>
        </Card.Group>
        // </Container>
    );
}

export default UserAcquisitionCard;


