import React, { useState, useCallback } from 'react';
import axios from 'axios';
import { useDropzone } from "react-dropzone";
import styles from "./styles/Home.module.css";
import { 
    Form, 
    Embed,
    Container,
} from 'semantic-ui-react'
import { useHistory } from 'react-router-dom';

function UploadArtForm({ currentUser, onCreateArtwork }){

    const history = useHistory() 
    const [title, setTitle] = useState("");
    const [image, setImage] = useState("");

    const onDrop = useCallback((acceptedFiles) => {
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({ 
        onDrop, 
        accepts: "image/*",
        multiple: false,
    })

    function handleSubmitNewArtwork(e) {
        e.preventDefault();
        axios.post("/artworks", {
                title: title,
                image: image,
                user_id: currentUser.id,
            })
            .then((response) => {
                onCreateArtwork(response.data)
                history.push("/")
            })
    }   

    return ( 
        <Container>
            <>
            <div {...getRootProps()} className={`${styles.dropzone}`}>
                <input {...getInputProps()}/>    
                Drop Zone
            </div>
            </>
            <br></br>
        <Form onSubmit={handleSubmitNewArtwork}>
            <br></br>
            <Form.Group widths="equal">
                <Form.Input 
                    label='title'
                    value={title}
                    placeholder='title'
                    type="text"
                    onChange={(e) => setTitle(e.target.value)}
                    />
                <Form.Input 
                    label='image-url'
                    value={image}
                    placeholder='image-url'
                    type="text"
                    onChange={(e) => setImage(e.target.value)}
                    />
                </Form.Group>
            <Form.Button>submit</Form.Button>
        </Form>
        <br></br>
        <Embed
            icon='paint brush'
            url='https://kleki.com/'
        />
        <br></br>
        <Embed
            icon='file pdf'
            url='https://docs.google.com/presentation/d/1MXF9c1oGW3kR93imVzaFhlsm_-HYOzQZlsfwPv67BGs/edit#slide=id.p'
        />
        <br></br>
        <Embed
            style={{height: 2800}}
            icon='stack exchange'
            url='https://ezgif.com/pdf-to-gif'
        /> 
        <br></br>
        </Container>
    );
}

export default UploadArtForm;


