import React from 'react'
import ArtCard from './ArtCard'
import { Container, Segment, Card } from 'semantic-ui-react'

function ArtPage(
    { 
        currentUser, 
        artworks, 
        onAcquireArtwork, 
    }
) {

  const uArtwork =
    artworks.map((artwork) => {
        // return <ArtCard
        return <Card 
                justify-content="center"
                width='300px'
                >
        {/* return <Card> */}
                    <ArtCard
                    // className='art-card'
                    key={artwork.id} 
                    artwork={artwork} 
                    currentUser={currentUser} 
                    onAcquireArtwork={onAcquireArtwork}
                    />
                </Card>

    })

    return(
        <div className='art-container' >
            <Container 
                // display="flex"
                // // align-items="center"
                // style={{ margin: 20 }}
                // justify-content="center"
                >
                {/* <Segment> */}
                    {uArtwork}
                {/* </Segment> */}
            </Container>
        </div>
    )
}

export default ArtPage;

