import React from 'react'

function CommentCard({ currentUser, content, author, comment, onDeleteComment }){

    const { id } = comment

    function handleDeleteCommentClick() {
        fetch(`http://localhost:3000/comments/${id}`, {
            method: "DELETE"
        })
            .then(res => res.json())
            .then(deletedComment => {
                onDeleteComment(deletedComment)
            })
    }


    return (
            <div>
                <h4>{content}</h4>
                <h4>-{author}</h4>
                {currentUser.id === comment.commenter_id ? (
                        <>
                            <button onClick={handleDeleteComment}>delete comment</button>
                        </>
                    ) : (
                        <>
                        </>
                    )}
            </div>
            )
}

export default CommentCard
