        users.map((user) => {
            user.acquisitions.map((acquisition) => {
                // return acquisition 
                return <AcquisitionCard
                acquisition
                key={user.id}
                userCollection={user.acquisitions}
                user={user}
                currentUser={currentUser} 
                onAcquireArtwork={onAcquireArtwork}
                userAcquisition={userAcquisition}
