import React from 'react'

function CommentCard({ key, content, author, comment, onDeleteComment }){

    const { id } = key

    function handleDeleteCommentClick() {
        fetch(`http://localhost:3000/comments/${id}`, {
            method: "DELETE"
        })
            .then(res => res.json())
            .then(deletedComment => {
                onDeleteComment(deletedComment)
            })
    }


    return (
            <div>
                <h4>{content}</h4>
                <h4>-{author}</h4>
            </div>
            )
}

export default CommentCard
