import React from 'react';
import { Link } from 'react-router-dom';
import { Dropdown, Button, Select } from 'semantic-ui-react';

function NavBar({ currentUser, setCurrentUser }) {
    function logout(){
        setCurrentUser(null);
    }
    const options = [
        { key:'artworks', value:'/', text:'Raregifs' },
        { key:'collections', value:'/users', text:'Collections' },
        { key:'storage', value:'/storage', text:'Storage' },
        { key:'upload', value:'/upload', text:'Upload' },        
    ]

    return(  
        <div>
            <Dropdown attached="top">
                <Select options={options} />
            </Dropdown>
            {/* {currentUser ? (
