import React, { useState } from 'react'
import { 
    Grid,
    Image,
    Button,
    Header,
    Modal,
  } from 'semantic-ui-react'

function StorageCardArt({ 
                artwork, 
                onDeleteArtwork, 
                onUpdateArtwork,
             }) {

    const [open, setOpen] = React.useState(false)
    const aUsers = artwork.acquiring_users.map((user) => {
        return (
            <div key={user.id}>{user.name}</div>
        )
    })

    const { id, title, image } = artwork
    const [updatedTitle, setUpdatedTitle] = useState(title)

    function handleDeleteClick() {
        fetch(`http://localhost:3000/artworks/${id}`, {
            method: "DELETE"
        })
            .then(res => res.json())
            .then(deletedArtwork => {
                onDeleteArtwork(deletedArtwork)
            })
        }

    function handleUpdateArtworkSubmit(e) {
        e.preventDefault();
        fetch(`http://localhost:3000/artworks/${id}`, {
            method: "PATCH",
            headers: {
            "Content-Type": "application/json",
            },
            body: JSON.stringify({ title: updatedTitle }),
        })
            .then((r) => r.json())
            .then((updatedArtwork) => {
                onUpdateArtwork(updatedArtwork)
            })
    }

    return (
        <Grid 
          stackable centered 
          style={{ 
            margin: 10,
            padding: 40,
          }}
          >
              <Modal
                onClose={() => setOpen(false)}
                onOpen={() => setOpen(true)}
                open={open}
                trigger={<Image size='large' src={image} />}
            >
                 <Modal.Content image >
                  <Image wrapped size='huge' src={image} alt={title}/>
                  <Modal.Description>
                    <Header>{title}</Header>
                    {/* <p>{user.name}</p> */}
                  </Modal.Description>
                </Modal.Content>
                <Modal.Actions>
                  <Button color='black' onClick={() => setOpen(false)}>
                    Nope
                  </Button>
                  <Button
                    content="Add it to the collection"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={handleAcquireArtworkClick}
                    positive
                  />
                </Modal.Actions>
                <img src={image} alt={title} />
                <h3>{title}</h3>
                <form onSubmit={handleUpdateArtworkSubmit}>
                    <input
                        type="text"
                        name="title"
                        autoComplete="off"
                        value={updatedTitle}
                        onChange={(e) => setUpdatedTitle(e.target.value)}
                    /><input type="submit" value="update title"/>
                </form>
                <button onClick={handleDeleteClick}>delete</button>
                <div>
                    {artwork.acquiring_users.length > 0? (
                        <>
                            <h4>collected by:{aUsers}</h4>
                        </>
                    ) : (
                        <>
                        </>
                    )}
                </div>
            </Modal>
        </Grid>
    )
}

export default StorageCardArt;
