import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import { Dropdown, Button, Select } from 'semantic-ui-react';

function NavBar({ currentUser, setCurrentUser }) {
    function logout(){
        setCurrentUser(null);
    }
    const options = [
        { key:'artworks', value:'/', text:'Raregifs' }
    ]

    return(  
        <div>
            <Dropdown attached="top" text="Raregifs">
                options={options}
            </Dropdown>
            {currentUser ? (
                <>
                <Button onClick={logout} >logout</Button>   
                <NavLink to="/account" exact className="button">
                    Account
                </NavLink>
                </>
            ) : (
                <>
                    <NavLink to="/login" exact className="button">
                        Login
                    </NavLink>
                    <NavLink to="/signup" exact className="button" >
                        Signup
                    </NavLink>
                </>
                )}
        </div>
    )
}

export default NavBar;

