import React from 'react';
import { 
  Grid,
  Image,
  Button,
  Header,
  Modal,
} from 'semantic-ui-react'

function ArtCard(
      { 
          artwork, 
          currentUser, 
          onAcquireArtwork, 
      }
) {
    console.log(artwork)

    const [open, setOpen] = React.useState(false)
    const { title, image, user } = artwork

    function handleAcquireArtworkClick(e) {
        e.preventDefault();
        fetch('http://localhost:3000/acquisitions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            user_id: currentUser.id,
            artwork_id: artwork.id,
          }),
        })
        .then(r => r.json())
        .then((newAcquisition) => onAcquireArtwork(newAcquisition))
      }

      return (
        <Grid 
          stackable centered columns={1}
          style={{ 
            margin: 10,
            padding: 40,
            // width: 300,
          }}
          >
            <Modal

                style={{ 
                      margin: 10,
                      padding: 40,
                      maxHeight: 300,
                    }}
                // size="large"
                // height="tall"
                onClose={() => setOpen(false)}
                onOpen={() => setOpen(true)}
                open={open}
                trigger={<Image size='large' src={image} />}
                // padding='20px'
                // justify-content='center'
            >
              {/* style={{ maxHeight: 500 }} */}
              {/* style={{ marginTop: '1rem' }} */}
              {/* style={{ position: relative }} */}
                  <Modal.Content image >
                  <Image wrapped size='huge' src={image} />
                  {/* <Image size='huge' src={image} wrapped /> */}
                  <Modal.Description>
                    <Header>{title}</Header>
                    <p>{user.name}</p>
                  </Modal.Description>
                </Modal.Content>
                <Modal.Actions>
                  <Button color='black' onClick={() => setOpen(false)}>
                    Nope
                  </Button>
                  <Button
                    content="Add it to the collection"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={handleAcquireArtworkClick}
                    // onClick={() => setOpen(false)}
                    positive
                  />
                </Modal.Actions>
            </Modal>
          </Grid>
       );
  }

export default ArtCard;


