import React, { useState } from 'react';
import { 
    Form, 
    Embed,
    Button,
} from 'semantic-ui-react'
// active storage
// import { DirectUpload } from 'activestorage'

function UploadArtForm({ currentUser, onCreateArtwork }){

    const [title, setTitle] = useState("");
    const [image, setImage] = useState("");

    function handleSubmitNewArtwork(e) {
        e.preventDefault();
        fetch("http://localhost:3000/artworks", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title: title,
                image: image,
                user_id: currentUser.id,
            }),
        })
            .then((res) => res.json())
            .then((newArtwork) => {
                onCreateArtwork(newArtwork)
                // console.log(newArtwork)
            })
    }

    function handleConvertClick(e) {
        e.preventDefault();
        fetch("https://api.convertio.co/convert", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                apikey: "43419917fb02bda8434dd02fdceb07cf",
                file: "http://google.com/",
                outputformat: "gif",
            }),
        })
            .then((res) => res.json())
            .then((convertedFile) => {
                console.log(convertedFile)
            })
        }



    return ( 
        <div>
            <br></br>
        <Form onSubmit={handleSubmitNewArtwork}>
            <br></br>
            <Form.Group widths="equal">
                <Form.Input 
                    // fluid label='title'
                    label='title'
                    value={title}
                    placeholder='title'
                    type="text"
                    onChange={(e) => setTitle(e.target.value)}
                    />
                <Form.Input 
                    // fluid label='image-url'
                    label='image-url'
                    value={image}
                    placeholder='image-url'
                    type="text"
                    onChange={(e) => setImage(e.target.value)}
                    />
                </Form.Group>
            <Form.Button>submit</Form.Button>
        </Form>
        <br></br>
        <Embed
            icon='paint brush'
            // placeholder='click play button to start a new drawing'
            url='https://kleki.com/'
        />
        <br></br>
        <Embed
            icon='file pdf'
            url='https://docs.google.com/presentation/d/1-fqAEHm5nJW0QQgHCvb_jyOfmQG98tZNu6iodqoJLFk/edit#slide=id.p'
        />
        <br></br>
        <Embed
            style={{}}
            icon='stack exchange'
            url='https://ezgif.com/pdf-to-gif'
        /> 
        {/* <Button
            onClick={handleConvertClick}
        >convert pdf to gif</Button> */}
        </div>
    );
}

export default UploadArtForm;



