import React, { useEffect, useState } from 'react';
import StorageList from './StorageList'

function StoragePage({ currentUser, acquisitions, setAcquisitions }) {

  userAcquisitions = acquisitions.map((acquisition) => {
    if (acquisition.user.id === currentUser.id) {
      return acquisition
    }
  })

  function handleDeleteAcquisition(id) {
    const updatedAcquisitionsArray = storedAcquisitions.filter((acquisition) => acquisition.id !== id);
    setAcquisitions(updatedAcquisitionsArray);
  } 

  function handleUpdateArtwork(updatedArtwork) {
    const updatedArtworksArray = userArtworks.map((artwork) => {
      if (artwork.id === updatedArtwork.id) {
          return updatedArtwork;
      } else {
          return artwork;
      }
    })
    setUserArtworks(updatedArtworksArray);
  }

  function handleDeleteArtwork(id) {
    const updatedArtworksArray = UserArtworks.filter((artwork) => artwork.id !== id);
    setUserArtworks(updatedArtworksArray);
  }  

  return (
      <div className="storage" >
        <StorageList
           currentUser={currentUser}
           UserArtworks={storedUserArtworks}
           storedAcquisitions={storedAcquisitions}
           onDeleteAcquisition={handleDeleteAcquisition}
           onDeleteArtwork={handleDeleteArtwork}
           onUpdateArtwork={handleUpdateArtwork}
        />
      </div>
  );
}

