    currentUser, 
    onAcquireArtwork,
}){
    console.log(users)

    let [eachAcquisitionUser, setEachUserAcquisition] = useState([])

   let userCard =
        users.map((user) => {
        return user.acquisitions.map((acquisition) => 
               (
                    <AcquisitionCard
                    key={user.id}
                    user={user}
                    userAcquisition={acquisition}
                    currentUser={currentUser} 
                    onAcquireArtwork={onAcquireArtwork}
                />
            )
        )}
    )

    return(
        <div>
            <h4>{acquisition.user.name}</h4>
                {userCard}
        </div>
    );
}

