import React from 'react'

function ArtCard({ 
  artwork, 
  currentUser, 
  acquisitions, 
  setAcquisitions,
  // onAcquireArtwork, 
}) {

    const { title, image, user } = artwork
    console.log(currentUser)
    function handleAcquireArtworkClick(e) {
        const acquisition = {
          user_id: currentUser.id,
          artwork_id: artwork.id,
        }
        e.preventDefault();
        fetch('http://localhost:3000/acquisitions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
             acquisition
          }),
        })
        .then(r => r.json())
        .then(newAcquisition => {
            let updatedAcquisitionsArray = [newAcquisition, ...acquisitions]
          setAcquisitions(updatedAcquisitionsArray)
        })
      }

    return (
        <div className="art-card">
            <img src={image} alt={title} />
            <h3>{title}</h3>
            <h3>{user.name}</h3>
            <button onClick={handleAcquireArtworkClick}>acquire image</button>
        </div>
    );
}

export default ArtCard;
