import React from 'react';
import { 
  Grid,
  Image,
  Button,
  Header,
  Modal,
} from 'semantic-ui-react'

function ArtCard(
      { 
          artwork, 
          currentUser, 
          onAcquireArtwork, 
      }
) {

    const [open, setOpen] = React.useState(false)
    const { title, image, user } = artwork

    function handleAcquireArtworkClick(e) {
        e.preventDefault();
        fetch('http://localhost:3000/acquisitions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            user_id: currentUser.id,
            artwork_id: artwork.id,
          }),
        })
        .then(r => r.json())
        .then((newAcquisition) => onAcquireArtwork(newAcquisition))
      }

      let columns = (4, (i) => (
        <Grid.Column key={i}>
           <Grid.Column>
            <Modal
                onClose={() => setOpen(false)}
                onOpen={() => setOpen(true)}
                open={open}
                trigger={<Image size='small' src={image}/>}
            >
                <Modal.Content image>
                  <Image size='large' src={image} wrapped />
                  <Modal.Description>
                    <Header>{title}</Header>
                    <p>{user.name}</p>
                  </Modal.Description>
                </Modal.Content>
                <Modal.Actions>
                  <Button color='black' onClick={() => setOpen(false)}>
                    Nope
                  </Button>
                  <Button
                    content="Add it to the collection"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={handleAcquireArtworkClick}
                    // onClick={() => setOpen(false)}
                    positive
                  />
                </Modal.Actions>
            </Modal>
          </Grid.Column>       
        </Grid.Column>
      ))

      return (
        <Grid container columns={3}>
          {columns}
        </Grid>
    );
}

export default ArtCard;


