import React from 'react'
import AcquisitionCard from './AcquisitionCard'

function AcquisitionsList({ 
    users,
    currentUser, 
    onAcquireArtwork,
}){

    const userCard =
        users.map((user) => {
            return (
                <AcquisitionCard
                key={user.id}
                user={user}
                // userAcquisition={user.acquisitions.map((acquisition) => 
                //     <li>{acquisition}</li>)}
                currentUser={currentUser} 
                onAcquireArtwork={onAcquireArtwork}
                />
            )
    })

    return(
        <div>
             <h4>The Collections</h4>
             {userCard}
            {/* {userAcquisitions} */}
        </div>
    );
}

