import React, { useEffect, useState } from 'react';
import StorageCardArt from './StorageCardArt';
import StorageList from './StorageList'

function StoragePage(
    { currentUser, 
      onUpdateArtwork, 
      onDeleteArtwork, 
      onDeleteAcquisition,
    }) {

    const uArtworkCard = 
        currentUser.artworks.map((artwork) => {
            return <StorageCardArt 
            key={artwork.id}
            artwork={artwork}
            onUpdateArtwork={onUpdateArtwork}
            onDeleteArtwork={onDeleteArtwork}
            />
        })

    let uAcquisitions = currentUser.acquisitions.map((acquisition) =>
      (acquisition.artwork)
    )

    setUserAcquisitions(uAcquisitions)


    return (
        <div className="storage" >
          <StorageList
            currentUser={currentUser}
            onDeleteAcquisition={onDeleteAcquisition}
            onDeleteArtwork={onDeleteArtwork}
            onUpdateArtwork={onUpdateArtwork}
          />
        </div>
    );
}

export default StoragePage;

