import React, { useState } from 'react'

function ArtCard({ artwork, user, onDeleteArtwork, onUpdateArtwork }) {
    const { title, image, id } = artwork
    const [updatedTitle, setUpdatedTitle] = useState(title)



    function handleDeleteClick() {
        fetch(`http://localhost:6001/artworks/${id}`, {
          method: "DELETE",
        });
        onDeleteArtwork(id);
      }    

    function handleUpdateArtworkSubmit(e) {
        e.preventDefault();
        fetch(`http://localhost:6001/artworks/${id}`, {
            method: "PATCH",
            headers: {
            "Content-Type": "application/json",
            },
            body: JSON.stringify({ title: updatedTitle }),
        })
            .then((r) => r.json())
            .then((updatedArtwork) => {
            onUpdateArtwork(updatedArtwork);
            });
    }

    return (
        <div className="art-card">
            { user.id === artwork.user.id ? (
                        <>
                            <img src={image} alt={title} />
                            <form onSubmit={handleUpdateArtworkSubmit}>
                                <input
                                    type="text"
                                    name="title"
                                    autoComplete="off"
                                    value={updatedTitle}
                                    onChange={(e) => setUpdatedArtwork(e.target.value)}
                                /><input type="submit" value="update title"/>
                                <h3>{artwork.user.name}</h3>
                            </form>
                            <button onClick={handleDeleteClick}>delete image</button>
                        </>
                    ) : (
                        <>
                            <img src={image} alt={title} />
                            <h3>{title}</h3>
                            <h3>{artwork.user.name}</h3>
                            <button>acquire image</button>
                        </>
                    )}
        </div>
    );
}

export default ArtCard;
