import React from 'react'
import { Card, Button, G } from 'semantic-ui-react'

function CommentCard({ 
            cUId, 
            commenter_id, 
            content, 
            author, 
            commentID, 
            onDeleteComment 
        }){

    function handleDeleteCommentClick() {
        fetch(`http://localhost:3000/comments/${commentID}`, {
            method: "DELETE"
        })
            .then(res => res.json())
            .then(deletedComment => {
                onDeleteComment(deletedComment)
            })
    }

    return (
        // <div>
        //     <div>
       <Grid
        style={{ 
            margin: 10,
            padding: 20,
        // width: 300,
        }}
       >
            <Card.Group >
                <Card>
                    <Card.Content>
                        <Card.Header>{content}</Card.Header>
                        <Card.Description>-{author}</Card.Description>
                        {cUId === commenter_id ? (
                            <>
                                <Button 
                                    onClick={handleDeleteCommentClick}
                                    size="small"
                                    icon="trash"
                                    >
                                </Button>
                            </>
                        ) : (
                            <>
                            </>
                        )}
                    </Card.Content>
                </Card>
            </Card.Group>
       </Grid>
        //         <br></br>
        //     </div>
        // </div>
    )
}

export default CommentCard






