                columns={2} 
                doubling 
                stackable
                width={20}
            >
                <Grid.Column>
                    <Segment>{uAcquisitions}</Segment>
                </Grid.Column>
                <Grid.Column>
                    <Segment><CommentForm 
                            eachUserID={eachUser.id} 
                            cUId={cUId} 
                            onAddComment={onAddComment}
                            />
                    </Segment>
                </Grid.Column>
            </Grid>
        </Grid.Column>
    </Grid>
