import React from 'react';
import { 
    Grid,
    Card,
    Image,
    Button,
    Header,
    Modal,
  } from 'semantic-ui-react'

function UserAcquisitionCard({ 
                aArtwork, 
                cUId, 
                onAcquireArtwork,
             }) {

    function handleAcquireArtworkClick(e) {
        e.preventDefault();
        fetch('http://localhost:3000/acquisitions', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user_id: cUId,
                artwork_id: aArtwork.id
            }),
        })
            .then(r => r.json())
            .then((newUserAcq) => onAcquireArtwork(newUserAcq))
    }

    return (
        <Grid 
            // relaxed columns={1}
            // centered columns={2}
            // style={{ 
            // margin: 10,
            // padding: 40,
            // // width: 300,
            // }}
        >
            <Grid.Column width={3}>
                <Card>
                <Image size='small' src={aArtwork.image} alt={aArtwork.title} />
                <Card.Content>
                    <Card.Header
                        style={{
                            // position: 'relative',
                            // width: '90px',
                            // textAlign: 'left',
                            // display: 'block',
                            fontSize: 'small',
                            color: 'red',
                        }}
                    >
                        {aArtwork.title}
                    </Card.Header>
                </Card.Content>
                <h4>{aArtwork.user_name}</h4>
                <div>
                    {cUId ? (
                        <>
                        <button onClick={handleAcquireArtworkClick}>
                            acquire image
                        </button>
                        </>
                    ) : (
                        <>
                        </>
                    )}
                </div>
                <br></br>
                <br></br>
                </Card>
            </Grid.Column>
        </Grid>
    );
}

export default UserAcquisitionCard;



