import React, { useState } from 'react';

function UploadArtForm(){
// function UploadArtForm({ onAddArtwork }){
// function UploadArtForm({ user, onAddArtwork, artworks, setArtworks }){
    const [title, setTitle] = useState("");
    const [image, setImage] = useState("");
    // const { id } = user;

    function handleSubmit(e) {
        e.preventDefault();
        fetch("http://localhost:3000/artworks", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title: title,
                image: image,
            }),
        })
            .then((res) => res.json())
            .then((newArtwork) => onAddArtwork(newArtwork));
    }

      function handleAddArtwork(newArtwork) {
        const updatedArtworksArray = [...artworks, newArtwork];
        setArtworks(updatedArtworksArray);
    }

    return ( 
        <div className="upload-art-form">
            <h1>upload: jpeg or gif, square format, under 2MB</h1>
            <form onSubmit={handleSubmit} onA>
                <input
                    type="text"
                    title="title"
                    placeholder="title (optional)"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <input
                    type="text"
                    title="image"
                    placeholder="enter image url/ gif url"
                    value={image}
                    onChange={(e) => setImage(e.target.value)}
                />
                <button type="submit">Add Image</button>
            </form>
        </div>
    );
}

export default UploadArtForm;
