import React, { useState } from 'react'

function AcquisitionStorageCard({ 
    userArtwork, 
    currentUser, 
    userAcquisition,
//   setAcquisitions,
//   onDeleteArtwork={onDeleteArtwork},
//   onUpdateArtwork={onUpdateArtwork},
//   onDeleteAcquisition={onDeleteAcquisition}

}) {
    const [updatedTitle, setUpdatedTitle] = useState("")
    const [userAcquisitions, setUserAcquisitions] = useState([])
    const [userArtworks, setUserArtworks] = useState([])

    // const [updatedTitle, setUpdatedTitle] = useState(title)
    // const [userAcquisitions, setUserAcquisitions] = useState(userAcquisitions)
    // const [userArtworks, setUserArtworks] = useState(userArtworks)

    // const { title } = userArtwork


    // function handleDeleteArtwork(id) {
    //     const updatedArtworksArray = artworks.filter((artwork) => artwork.id !== id);
    //     setArtworks(updatedArtworksArray);
    // } 

//       function handleDeleteAcquisitionClick(id) {
//     const updatedAcquisitionsArray = acquisitions.filter((acquisition) => acquisition.id !== id);
//       setAcquisitions(updatedAcquisitionsArray);
//   } 

    // function handleUpdateArtwork(updatedArtwork) {
    //     const updatedArtworksArray = artworks.map((artwork) => {
    //     if (artwork.id === updatedArtwork.id) {
    //         return updatedArtwork;
    //     } else {
    //         return artwork;
    //     }
    //     })
    //     setUserArtworks(updatedArtworksArray);
    // }

    function handleDeleteAcquisitionClick() {
        fetch(`http://localhost:3000/acquisitions/${userAcquisition.id}`, {
            method: "DELETE",
        });
        const updatedAcquisitionsArray = userAcquisitions.filter((acquisition) => acquisition.id !== userAcquisition.id);
        setUserAcquisitions(updatedAcquisitionsArray);
        // onDeleteAcquisition(artwork.acquisition.id);
    }

    // function handleDeleteClick() {
    //     fetch(`http://localhost:3000/artworks/${userArtwork.id}`, {
    //       method: "DELETE",
    //     });
    //     const updatedArtworksArray = userArtworks.filter((artwork) => artwork.id !== userArtwork.id);
    //     setUserArtworks(updatedArtworksArray);
    //     // onDeleteArtwork(id);
    //   }    

    function handleUpdateArtworkSubmit(e) {
        e.preventDefault();
        fetch(`http://localhost:3000/artworks/${userArtwork.id}`, {
            method: "PATCH",
            headers: {
            "Content-Type": "application/json",
            },
            body: JSON.stringify({ title: updatedTitle }),
        })
            .then((r) => r.json())
            .then((updatedArtwork) => {
                const updatedUserArtworksArray = userArtworks.map((artwork) => {
                    if (artwork.id === updatedArtwork.id) {
                        return updatedArtwork;
                    } else {
                        return artwork;
                    }
                    })
                    setUserArtworks(updatedUserArtworksArray);
            // onUpdateArtwork(updatedArtwork);
            });
    }

    return (
        <div className="storage-card">
            <img src={userAcquisition.artwork.image} alt={userAcquisition.artwork.title} />
            <h3>{userAcquisition.artwork.title}</h3>
            <h3>{userAcquisition.artwork.user_name}</h3>
            <button onClick={handleDeleteAcquisitionClick}>remove from collection</button>
        </div>
    );
}

export default AcquisitionStorageCard;
