      const uArtworkCard =
          userArtworks.map((artwork) => { 
              return <StorageCardArt 
                key={artwork.id}
                artwork={artwork}
                onUpdateArtwork={onUpdateArtwork}
                onDeleteArtwork={onDeleteArtwork}
                // setUserArtworks={setUserArtworks}
              />
          })

      const uAcquisitionCard = 
          userAcquisitions.map((acquisition) => {
