import React, { useState } from 'react'
import UserAcquisitionCard from './UserAcquisitionCard'
import CommentForm from './CommentForm'
import CommentCard from './CommentCard'

function UserList({ eachUser, currentUser, onAcquireArtwork, userCollection,  }){

    const [comments, setComments] = useState([eachUser.comments_received])

    function handleAddComment(newComment) {
        const updatedCommentsArray = [newComment, ...comments];
        setComments(updatedCommentsArray)
    } 

    function handleDeleteComment(id) {
        const updatedCommentsArray = comments.filter((comment) => comment.id !== id);
        setComments(updatedCommentsArray);
    } 

   let userCard =
        userCollection ? (userCollection.map((acquisition) => {
        return <UserAcquisitionCard
                    key={acquisition.id}
                    acquisition={acquisition}
                    currentUser={currentUser} 
                    onAcquireArtwork={onAcquireArtwork}
                />
        })) 
        : (null)

    let userComments =
        comments ? (comments.map((comment) => {
            return( 
                <div>
                    <CommentCard
                        key={comment.id}
                        currentUser={currentUser}
                        comment={comment}
                        content={comment.content}
                        author={comment.author}
                        commenter_id={comment.commenter_id}
                        onDeleteComment={handleDeleteComment}
                    />
                </div>
            )
        }
        )) : (null)

    return(
        <div>
            <h2>The {eachUser.name} Collection</h2>
                {userCard}
            <h3>Comments on {eachUser.name}'s collection</h3>    
                {userComments}
                <CommentForm 
                    eachUser={eachUser} 
                    currentUser={currentUser} 
                    onAddComment={handleAddComment}
                    onDeleteComment={handleDeleteComment}
                    />
                <br></br>
        </div>
    );
}

export default UserList;



