import React, { useEffect, useState } from 'react';
import { Switch, Route } from 'react-router-dom';
import NavBar from './NavBar';
// import ArtPage from './ArtPage';
import ArtList from './ArtList';
import Login from './Login';
import Upload from './UploadArtForm';
import Signup from './Signup';
import UserPage from './UserPage';
// import AcquisitionsList from './AcquisitionsList'
import Storage from './Storage';

function App() {
  const [currentUser, setCurrentUser] = useState(null)
  const [artworks, setArtworks] = useState([]);
  const [acquisitions, setAcquisitions] = useState([])
  // const [users, setUsers] = useState([])

  useEffect(() => {
    fetch("http://localhost:3000/me")
    .then(r => r.json())
    .then(user => {
      setCurrentUser(user)
    })
  }, [])

  useEffect(() => {
      fetch('http://localhost:3000/artworks')
        .then(r => r.json())
        .then((artworksArray) => {
            setArtworks(artworksArray);
        });
      }, []) 

  useEffect(() => {
      fetch('http://localhost:3000/acquisitions')
          .then(r => r.json())
          .then(acquisitionsArray => {
              setAcquisitions(acquisitionsArray);
          })
  }, [])

  function handleAddAcquisition(newAcquisition) {
    const updatedAcquisitionsArray = [newAcquisition, ...acquisitions];
    setAcquisitions(updatedAcquisitionsArray)
  }

  // function handleDeleteAcquisition(id) {
  //   // i have the id for the acquisition i want to delete but not the user_id (but doesnt acquisition have the user_id?)
  //   // const updatedAcquisitionsArray = user.acquisitions.filter((acquisition) => acquisition.id !== id);
  //   const updatedAcquisitionsArray = acquisitions.filter((acquisition) => acquisition.id !== id);
  //   setAcquisitions(updatedAcquisitionsArray);
  //   // setUsers(updatedAcquisitionsArray);
  // } 

  function handleAddArtwork(newArtwork) {
    const updatedArtworksArray = [newArtwork, ...artworks];
    setArtworks(updatedArtworksArray)
  }

  // function handleUpdateArtwork(updatedArtwork) {
  //   const updatedArtworksArray = artworks.map((artwork) => {
  //     if (artwork.id === updatedArtwork.id) {
  //         return updatedArtwork;
  //     } else {
  //         return artwork;
  //     }
  //   })
  //   setArtworks(updatedArtworksArray);
  // }

  // function handleDeleteArtwork(id) {
  //   const updatedArtworksArray = artworks.filter((artwork) => artwork.id !== id);
  //   setArtworks(updatedArtworksArray);
  // }  


   return (
    <div className="app">
        <NavBar currentUser={currentUser} setCurrentUser={setCurrentUser} />
            <Switch>
              <Route exact path="/login">
                <Login setCurrentUser={setCurrentUser}/>
              </Route>
              <Route exact path="/" >
                {/* <ArtPage  */}
                <ArtList 
                  currentUser={currentUser} 
                  artworks={artworks} 
                  // acquisitions={acquisitions}
                  // setAcquisitions={setAcquisitions}
                  // setArtworks={setArtworks}
                  onAcquireArtwork={handleAddAcquisition}
                  />
              </Route>
              {/* <Route exact path="/acquisitions"> */}
              <Route exact path="/users">
                <UserPage
                  // users={users}
                  currentUser={currentUser}
                  // acquisitions={acquisitions}
                  // setAcquisitions={setAcquisitions}
                  onAcquireArtwork={handleAddAcquisition}
                />
              </Route>
              <Route exact path="/upload">
                <Upload 
                  currentUser={currentUser}
                  onCreateArtwork={handleAddArtwork}
                />
              </Route>
              <Route exact path="/storage">
                <Storage
                  currentUser={currentUser}
                  acquisitions={acquisitions}
                  setAcquisitions={setAcquisitions}
                  onDeleteAcquisition={handleDeleteAcquisition}
                  artworks={artworks}
                  setArtworks={setArtworks}
                  onDeleteArtwork={handleDeleteArtwork}
                  onUpdateArtwork={handleUpdateArtwork}
                  />
              </Route>
              <Route exact path="/signup">
                <Signup 
                  setCurrentUser={setCurrentUser}/>
              </Route>
            </Switch>
    </div>
  );
}
export default App;
